<script>
    $(document).ready(function(){

    $('#carousel').slick({
        infinite: true,
        speed: 300,
        slidesToShow: 1,
        centerMode: true,
        arrows: true,
        adaptiveHeight: true
    });

    $(".btn_blue").on('click', function(e){
        $(".pop_up_form").toggleClass('active');
        e.preventDefault();
    });

    $(".close").on('click',function(e){
        $(".pop_up_form").removeClass('active');
        e.stopPropagation();
        return false;
    });

    $('.btn_toggle').on('click', function(){
        $('.wrapper_nav').toggleClass('active');
    });

    $('.wrapper_nav>a').on('click', function(e){
        $(this).removeClass('active');

        e.preventDefault();
    });

    $('.popup_training').hide();

    $('figure.center figcaption a').on('click', function(e){
        $('.popup_training').show();
        e.preventDefault();
    });

    $('.close_train').on('click', function(){
        $('.popup_training').hide();
    })


});
</script>
